<script>

export default {
  name: 'Header',
  props: {
  },
  data: function(){
    return {
      hasScroll:false
    }
  },
  methods: {
    checkScroll: function(){
      if (window.scrollY>40) {
        this.hasScroll=true;
      } else {
        this.hasScroll=false;
      }
    }
  },
  mounted: function(){
    this.checkScroll();
    window.addEventListener('scroll', function(){
      this.checkScroll();
    }.bind(this))
  }
}

</script>

